//PR.cp#include "PRR.h"#include <cmath>//--------------------------------------------------------#pragma mark		=== COMMON FUNCTIONS ===//--------------------------------------------------------doublePRRfluid::Tr_VrPr(const double &vr,				  const double &Pr){	return T_vP_a(vr*vc,Pr*Pc)/Tc;}//------------------------------------------voidPRRfluid::vr_PrTr(const double &Pr,				const double &Tr,				double &vrl,				double &vrg){	double vl,vg;	v_PT(Pr*Pc,Tr*Tc,vl,vg);	vl/=vc;	vg/=vc;}//------------------------------------------doublePRRfluid::vr_Prhr(	const double &Pr,					const double &hr){	return v_Ph_a(Pr*Pc,hr*hc)/vc;}//------------------------------------------doublePRRfluid::sr_Prhr(	const double &Pr,					const double &hr){	return s_Ph_a(Pr*Pc,hr*hc)/sc;}//------------------------------------------doublePRRfluid::hr_vrPr(	const double &vr,					const double &Pr){	return h_vP_a(vr*vc,Pr*Pc)/hc;}//------------------------------------------doublePRRfluid::Pr_vrTr(const double &vr,				const double &Tr){	return P_vT_a(vr*vc,Tr*Tc)/Pc;}//------------------------------------------doublePRRfluid::ur_vrTr(const double &vr,				const double &Tr){	return u_vT_a(vr*vc,Tr*Tc)/uc;}//------------------------------------------doublePRRfluid::sr_vrTr(const double &vr,				const double &Tr){	return s_vT_a(vr*vc,Tr*Tc)/sc;}//------------------------------------------doublePRRfluid::Pr_vrhr(	const double &vr,					const double &hr){	return P_vh(vr*vc,hr*hc)/Pc;}//------------------------------------------doublePRRfluid::Pr_vrsr(	const double &vr,					const double &sr){	return P_vs(vr*vc,sr*sc)/Pc;}//------------------------------------------doublePRRfluid::Tr_vrsr(	const double &vr,					const double &sr){	return T_vs(vr*vc,sr*sc)/Pc;}//------------------------------------------doublePRRfluid::Tr_vrur(	const double &vr,					const double &ur){ return T_vu(vr*vc,ur*uc)/Tc;}//------------------------------------------doublePRRfluid::Tr_Prsr(	const double &Pr,					const double &sr){	return T_Ps_a(Pr*Pc,sr*sc)/Tc;}//------------------------------------------doublePRRfluid::vr_Prsr(	const double &Pr,					const double &sr){	return v_Ps_a(Pr*Pc,sr*sc)/vc;}//------------------------------------------doublePRRfluid::hr_Prsr(	const double &Pr,					const double &sr){	return h_Ps(Pr*Pc,sr*sc)/hc;}//------------------------------------------doublePRRfluid::Pr_Trhr(const double &Tr,				const double &hr){	return P_Th_a(Tr*Tc,hr*hc)/Pc;}//------------------------------------------doublePRRfluid::hr_PrTr(const double &Pr,				const double &Tr){	return h_PT_a(Pr*Pc,Tr*Tc)/hc;}//------------------------------------------doublePRRfluid::Tr_hrsr(const double &hr,				const double &sr){	return T_hs_a(hr*hc,sr*sc)/Tc;}//------------------------------------------doublePRRfluid::vr_Trsr(const double &Tr,				const double &sr){	return v_Ts_a(Tr*Tc,sr*sc)/vc;}//--------------------------------------------------------#pragma mark		=== VAPOR ZONE FUNCTIONS ===//--------------------------------------------------------doublePRRfluid::Pr_VrTr_g(const double &vr,				const double &Tr){	return P_vT_a(vr*vc,Tr*Tc)/Pc;}//------------------------------------------doublePRRfluid::Vr_PrTr_g(	const double &Pr,					const double &Tr){	return v_PT_g(Pr*Pc,Tr*Tc)/vc;}//------------------------------------------doublePRRfluid::ur_PrTr_g(	const double &Pr,					const double &Tr){	return u_PT_g(Pr*Pc,Tr*Tc)/uc;}//------------------------------------------doublePRRfluid::hr_PrTr_g(	const double &Pr,					const double &Tr){	return h_PT_g(Pr*Pc,Tr*Tc)/hc;}//------------------------------------------doublePRRfluid::sr_PrTr_g(	const double &Pr,					const double &Tr){	return s_PT_g(Pr*Pc,Tr*Tc)/sc;}//------------------------------------------doublePRRfluid::vr_Prhr_g(	const double &Pr,					const double &hr){	return v_Ph_a(Pr*Pc,hr*hc)/vc;}//------------------------------------------doublePRRfluid::Tr_VrPr_g(	const double &vr,					const double &Pr){	return T_vP_a(vr*vc,Pr*Pc)/Tc;}//--------------------------------------------------------#pragma mark === DEW LINE FUNCTIONS  ===//--------------------------------------------------------doublePRRfluid::Vr_Tr_gs(const double &Tr){	return v_T_gs(Tr*Tc)/vc;}//--------------------------------------------------------#pragma mark === SATURATION LINE FUNCTIONS ===//--------------------------------------------------------doublePRRfluid::Tr_Pr_s(const double &Pr){	return T_P_s(Pr*Pc)/Tc;}//------------------------------------------doublePRRfluid::Pr_Tr_s(const double &Tr){	return P_T_s(Tr*Tc)/Pc;}//--------------------------------------------------------#pragma mark === BUBBLE LINE FUNCTIONS  ===//--------------------------------------------------------doublePRRfluid::Vr_Tr_ls(const double &Tr){	return v_T_ls(Tr*Tc)/vc;}//------------------------------------------doublePRRfluid::Tr_Vr_ls(const double &Vr){	return T_v_ls(Vr*vc)/Tc;}//--------------------------------------------------------#pragma mark		=== LIQUID ZONE FUNCTIONS ===//--------------------------------------------------------doublePRRfluid::ur_PrTr_l(	const double &Pr,					const double &Tr){	return u_PT_l(Pr*Pc,Tr*Tc)/uc;}//------------------------------------------doublePRRfluid::hr_PrTr_l(	const double &Pr,					const double &Tr){	return h_PT_l(Pr*Pc,Tr*Tc)/hc;}//------------------------------------------doublePRRfluid::sr_PrTr_l(	const double &Pr,					const double &Tr){	return s_PT_l(Pr*Pc,Tr*Tc)/sc;}//------------------------------------------doublePRRfluid::Pr_VrTr_l(	const double &vr,					const double &Tr){	return P_vT(vr*vc,Tr*Tc)/Pc;}//------------------------------------------doublePRRfluid::Vr_PrTr_l(	const double &Pr,					const double &Tr){	return v_PT_l(Pr*Pc,Tr*Tc)/vc;}//------------------------------------------doublePRRfluid::Tr_VrPr_l(	const double &vr,					const double &Pr){	return T_vP(vr*vc,Pr*Pc)/Tc;}