#include <math.h>#include <iostream>#define MAXIT 30float secante(float (*func)(float), float Xmin, float Xmax, float DeltaX){	int j;	float fl,fr,dx,swap,xl,rts;	fl=(*func)(Xmin);	fr=(*func)(Xmax);	if (fabs(fl) < fabs(fr)) {		rts=Xmin;		xl=Xmax;		swap=fl;		fl=fr;		fr=swap;	} else {		xl=Xmin;		rts=Xmax;	}	for (j=1;j<=MAXIT;j++) {		dx=(xl-rts)*fr/(fr-fl);		xl=rts;		fl=fr;		rts += dx;		fr=(*func)(rts);		if (fabs(dx) < DeltaX || fr == 0.0) return rts;	}	std::cout<<"Maximum number of iterations exceeded in secante"<<std::endl;	return 0.0;}#undef MAXIT